const fileInput=document.getElementById('fileInput');const fileUploadZone=document.getElementById('fileUploadZone');const fileSelected=document.getElementById('fileSelected');const uploadForm=document.getElementById('uploadForm');const animationPlaceholder=document.getElementById('animationPlaceholder');fileUploadZone.addEventListener('click',()=>fileInput.click());fileUploadZone.addEventListener('dragover',e=>{e.preventDefault();fileUploadZone.classList.add('dragover');});fileUploadZone.addEventListener('dragleave',e=>{e.preventDefault();fileUploadZone.classList.remove('dragover');});fileUploadZone.addEventListener('drop',e=>{e.preventDefault();fileUploadZone.classList.remove('dragover');if(e.dataTransfer.files.length){fileInput.files=e.dataTransfer.files;showSelectedFile();}});fileInput.addEventListener('change',showSelectedFile);function showSelectedFile(){if(fileInput.files.length){fileSelected.textContent='Selected: '+fileInput.files[0].name;fileSelected.style.display='';}else{fileSelected.textContent='';fileSelected.style.display='none';}}uploadForm.addEventListener('submit',function(e){e.preventDefault();if(!fileInput.files.length)return;animationPlaceholder.style.display='';setTimeout(()=>{animationPlaceholder.innerHTML='<i class="fas fa-check-circle" style="color:#059669;"></i>Your animated video lesson is ready!(Demo placeholder)';},3000);});