$(function(){$('input[name="myCourse"]').autocomplete({source:function(request,response){$.ajax({url:"autocomplete.php",dataType:"json",data:{term:request.term},success:function(data){response(data)}})},minLength:2,delay:200})});document.getElementById('saveSearchBtn').addEventListener('click',function(){const course=document.querySelector('input[name="myCourse"]').value;const institution=document.querySelector('select[name="institution"]').value;fetch('save-search.php',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({course,institution})}).then(res=>res.json()).then(data=>{document.getElementById('saveStatus').textContent=data.message}).catch(err=>{document.getElementById('saveStatus').textContent="Failed to save search."})});
