const dropZone=document.getElementById('dropZone');const fileInput=document.getElementById('fileInput');const fileInfo=dropZone.querySelector('.file-info');const generateBtn=document.getElementById('generateBtn');const generatedNotes=document.getElementById('generatedNotes');dropZone.addEventListener('dragover',(e)=>{e.preventDefault();dropZone.classList.add('dragover');});dropZone.addEventListener('dragleave',(e)=>{dropZone.classList.remove('dragover');});dropZone.addEventListener('drop',(e)=>{e.preventDefault();dropZone.classList.remove('dragover');if(e.dataTransfer.files&&e.dataTransfer.files.length>0){fileInput.files=e.dataTransfer.files;fileInfo.textContent=e.dataTransfer.files[0].name+' selected';}});fileInput.addEventListener('change',(e)=>{if(fileInput.files&&fileInput.files.length>0){fileInfo.textContent=fileInput.files[0].name+' selected';}else{fileInfo.textContent='.pdf,.docx,.pptx â€¢ Max 25MB';}});dropZone.addEventListener('click',()=>{fileInput.click();});generateBtn.addEventListener('click',()=>{generateBtn.disabled=true;generateBtn.innerHTML='Generating...<span class="spinner"></span>';generatedNotes.classList.remove('active');setTimeout(()=>{generateBtn.disabled=false;generateBtn.innerHTML='Generate';generatedNotes.innerHTML='<b>AI Notes Example:</b><br>-Key concept 1 explained.<br>-Important fact 2 summarized.<br>-Main takeaway 3 highlighted.';generatedNotes.classList.add('active');},1800);});